import React, {useState} from "react";
import './SendMessage.css'
import axios from "axios";
import {useNavigate, useParams} from "react-router-dom"

const baseURL =
  "https://strangers-things.herokuapp.com/api/2206-ftb-et-web-ft-b";

const SendMessage = ({userToken}) =>{

    const [messageText, setMessageText]= useState('')
    const {id}= useParams();
console.log(id)
    async function sendMessage(postId){

      await  fetch(`${baseURL}/${postId}/messages`, {
  method: "POST",
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${userToken}`
  },
  body: JSON.stringify({
    message: {
      content: {messageText}
    }
  })
}).then(response => response.json())
  .then(result => {
    // console.log(result);
  })
  .catch(console.error);

    }



    return(
        <div className='message-form'>

            <form>
                <label></label>

                <textarea  rows={20} cols={100} onChange={(event)=>setMessageText(event.target.value)}
                placeholder='Please enter message'></textarea>

                <button onClick={()=>sendMessage(id)}>Send Message</button>

            </form>
        </div>
    )
}

export default SendMessage;
