import React, { useEffect, useState } from "react";
import axios from "axios";

const baseURL =
  "https://strangers-things.herokuapp.com/api/2206-ftb-et-web-ft-b";

const Post = () => {
  const [allPosts, setAllPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const { data: posts } = await axios.get(`${baseURL}/posts`); console.log(posts);
        setAllPosts(posts);
      } catch (err) {
        console.error(err);
      }
    };
    fetchPosts();

  }, []);

  return allPosts.map((eachPost, idx) => {
    return (
      <div key={idx} class="post-title">
        <p>{eachPost.title}</p>
        <p>{eachPost.description}</p>
        <p>{eachPost.price}</p>
      </div>
    );
  });
};

export default Post;
