import './SendMessage.css'


const SendMessage = () =>{

    const [messageText, setMessageText]= useState('')


    return(
        <div className='message-form'>
            <form>
                <label></label>

                <textarea  rows={20} cols={100} onChange={(event)=>setMessageText(event.target.value)}></textarea>
            </form>
        </div>
    )
}

export default SendMessage;
